mapper.update:
update access_token
set expired_at=#{updatedExpiredAt,jdbcType=TIMESTAMP},
updated_at=#{updatedExpiredAt,jdbcType=TIMESTAMP}
where account_id=#{accountId,jdbcType=BIGINT}
order by id desc
limit 1

update access_token
set expired_at=#{updatedExpiredAt,jdbcType=TIMESTAMP},
updated_at=#{updatedExpiredAt,jdbcType=TIMESTAMP}
where id=#{id,jdbcType=BIGINT}
limit 1

update access_token
set expired_at=#{updatedExpiredAt,jdbcType=TIMESTAMP},

    account_id=#{accountId,jdbcType=BIGINT},

updated_at=#{updatedExpiredAt,jdbcType=TIMESTAMP}
where id=#{id,jdbcType=BIGINT}
limit 1

update access_token
set scope = #{scope,jdbcType=VARCHAR}
where access_token=#{accessToken,jdbcType=VARCHAR}
limit 1
